const path = require('path');

exports.cssFileNameToElmFileName = filename => {
  const dirName = path.dirname(filename);

  return path.join(dirName, 'Styles.elm');
};

exports.generateElmModuleForStyles = (cssModuleKeys, filename) => {
  const moduleName = path.basename(path.dirname(filename));
  return `module ${moduleName}.Styles exposing (Styles)

-- This is auto generated by Webpack
-- whenever ${path.basename(filename)} is edited
-- you do not want to edit this by hand

type alias Styles = {
  ${cssModuleKeys.map((k, i) => `${k}: String`).join(', ')}
}
  `;
};
